mapper.select:

SELECT *
FROM (
  SELECT rownum AS rn, emp_dept.*
  FROM (
    SELECT e.empno, e.name, e.grade, e.deptno, e.hiredate, d.deptName
    FROM emp e
    LEFT JOIN dept d ON e.deptno = d.deptno
    ORDER BY e.empno
  ) emp_dept
  WHERE rownum <= #{end}
)
WHERE rn >= #{start}

mapper.update:
update sal set 
(salbase=#{salbase}, salfood=#{salfood}, salbonus=#{salbonus}) 
where empno=#{empno}
 and salnum = (select max(salnum) from sal where empno = ${empno}
